definition.script:
pipeline {
    agent any 
    stages {
        stage('PIC-SURE API, PIC-SURE-HPDS UI') { 
            steps {
                parallel (
                    picsureapi:{
                        script {
                            def result = build job: 'PIC-SURE-API Build', parameters: [[$class: 'StringParameterValue', name: 'pipeline_build_id', value: env.BUILD_NUMBER]]
                            env.PSAJ_NUM = result.number
                        }
                    },
                    picsurehpdsui:{
                        script{
                            def result = build job: 'PIC-SURE-HPDS-UI Docker Build', parameters: [[$class: 'StringParameterValue', name: 'pipeline_build_id', value: env.BUILD_NUMBER]]
                            println(result)
                        }
                    }
                )
            }
        }
        stage('PSAMA, DataSTAGE UI and PIC-SURE HPDS'){
            steps {
                parallel (
                    picsurehpds:{
                        script {
                            def result = build job: 'PIC-SURE-HPDS Build', parameters: [[$class: 'StringParameterValue', name: 'pipeline_build_id', value: env.BUILD_NUMBER]]
                            env.PSHD_NUM = result.number
                        }
                    },
                    picsureauth:{
                        script{
                            def result = build job: 'PIC-SURE Auth Micro-App Build', parameters: [[$class: 'StringParameterValue', name: 'pipeline_build_id', value: env.BUILD_NUMBER]]
                            env.PSAMAJ_NUM = result.number
                        }
                    },
                    datastageui:{
                        script {
                            def result = build job: 'PIC-SURE-HPDS-UI-copdgene', parameters: [[$class: 'StringParameterValue', name: 'pipeline_build_id', value: env.BUILD_NUMBER]]
                            env.PSHUC_NUM = result.number
                        }
                    }
               )
            }
        }
    }
}
