definition.script:
pipeline {
    agent any 
    stages {
        stage('Initial Deps') { 
            steps {
                parallel (
                    picsureapijava:{
                        build('PIC-SURE API Java Build')
                    },
                    picsureauthjava:{
                        build('PIC-SURE Auth Micro-App Java Build')
                    },
                    picsurehpdsui:{
                        build('PIC-SURE-HPDS-UI Docker Build')
                    }
                )
            }
        }
        stage('Intermediate') { 
            steps {
                parallel (
                    picsurehpdsjava:{
                        build('PIC-SURE-HPDS Java Build')
                    },
                    picsureapidocker:{
                        build('PIC-SURE API Docker Build')
                    }
                )
            }
        }
        stage('Final'){
            steps {
                parallel (
                    picsureauthdocker:{
                        build('PIC-SURE Auth Micro-App Docker Build')
                    },
                    picsurecopdgeneui:{
                        build('PIC-SURE-HPDS-UI-copdgene')
                    }
               )
            }
        }
    }
}
